import openSocket from 'socket.io-client';
const  socket = openSocket('http://localhost:8000');

function subscribeToTimer(display) {
  socket.on('timer', hello => display(null, hello));
  socket.emit('subscribeToTimer');

}

function login(nickname) {
    socket.emit('login', nickname)
    console
}

function sendMessage(message) {
    socket.emit('sendMessage', message)
}

function getMessages(display) {
    socket.on('getMessages', messages => display(messages))
}

export { subscribeToTimer, login, sendMessage };
