import React, { Component } from 'react';
import Fab from '@material-ui/core/Fab'
import { sendMessage, getMessages } from '../api';

class Chat extends Component {

    constructor(props) {
        super(props);
            this.state = { 
                message: '',
                messages: []
            }

            this.handleChange = this.handleChange.bind(this);
            this.handleSubmit = this.handleSubmit.bind(this);
            getMessages((err, gMessages) => {
                this.setState({messages : gMessages})
            })
    }

    handleChange(event) {
        this.setState({ message : event.target.value })
    }

    handleSubmit(event) {
        event.preventDefault();
        sendMessage(this.state.message)
    }

    render() {
        const messsages = {
            messages
        }
        return (
            <div className="container chat h-100">
                <div id="chat">
                    {}
                </div>
                <form onSubmit={this.handleSubmit.bind(this)} className="">
                    <div className="form-group "> 
                        <input onChange={this.handleChange.bind(this)} type="text" id="message" placeholder="Message"/>
                    </div>
                    <Fab type="submit">Submit</Fab>
                </form>
            </div>
        );
    }
}

export default Chat;
