import React, { Component } from 'react';
import Fab from '@material-ui/core/Fab'
import { sendMessage, getMessages } from '../api';

class Chat extends Component {

    constructor(props) {
        super(props);
            this.state = { 
                message: '',
                messages: []
            }

            this.handleChange = this.handleChange.bind(this);
            this.handleSubmit = this.handleSubmit.bind(this);
            getMessages((err, gMessages) => {
                this.setState({messages : [...this.state.messages,...gMessages]})
            })
    }

    handleChange(event) {
        this.setState({ message : event.target.value })
    }

    handleSubmit(event) {
        event.preventDefault();
        sendMessage(localStorage.getItem('name') ,this.state.message)
    }

    render() {
        console.log(this.state.messages)
        return (
            <div className="container chat h-75">
                <div className="chat col-11 h-100">
                    {this.state.messages.map((message, index) => (

                        <div key={index}>{message}</div>
                    ))}
                </div>
                <form onSubmit={this.handleSubmit.bind(this)} className="row">
                    <div className="form-group col-10"> 
                        <input className="col-12" onChange={this.handleChange.bind(this)} type="text" id="message" placeholder="Message"/>
                    </div>
                </form>
            </div>
        );
    }
}

export default Chat;
