import React, { Component } from 'react';
import TextField from '@material-ui/core/TextField';
import Fab from '@material-ui/core/Fab';
import axios from 'axios';

class Home extends Component {


    constructor(props) {
        super(props);
        this.state = {
            nickname: ""
        }
        this.handleChange = this.handleChange.bind(this);
        this.onSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({nickname: event.target.value})
    }

    handleSubmit(event) {
        event.preventDefault();

        axios.post('http://localhost:4242/login', {name: this.state.nickname})
        .then(res => {
            console.log(res.data)
        })
    }

    render() {
        console.log(this.state.nickname)
        return(
            <section className="h-100 d-flex">
                <div className="h-50 col-3 d-flex flex-column m-auto">
                    <h1>Connect to IRC</h1>
                     <form className="h-50 col-3 d-flex flex-column m-auto" onSubmit={this.handleSubmit}>
                        <TextField
                        id="outlined-name"
                        label="Nickname"
                        className="mb-4"
                        margin="normal"
                        variant="outlined"
                        onChange={this.handleChange}
                        />
                        <Fab variant="extended" aria-label="Delete" >
                            Login
                        </Fab>
                    </form>
                </div>
            </section>
        );  
    }
}

export default Home;
